mapper.update:
update study set study_title = #{studyTitle}, study_leader_id= #{studyLeaderId}, study_status = #{studyStatus}, study_introduction = #{studyIntroduction}, study_startdate = #{studyStartdate},
study_enddate = #{studyEnddate}, study_goals = #{studyGoals}, study_type = #{studyType}, study_user_min = #{studyUserMin}, study_user_max = #{studyUserMax}, study_content = #{studyContent},
study_place = #{studyPlace}, study_category_id = #{studyCategoryId}
where study_id = #{studyId}

mapper.select:
select s.study_id studyId, study_title studyTitle, study_leader_id studyLeaderId, study_status studyStatus, study_introduction studyIntroduction, study_startdate studyStartdate, study_enddate studyEnddate, study_registdate studyRegistdate, study_goals studyGoals, study_type studyType , study_user_min studyUserMin, study_user_max studyUserMax, study_content studyContent, study_place studyPlace, study_category_id studyCategoryId, count(uls.study_id) as studyLike from study s left join user_like_study uls on s.study_id = uls.study_id
s.study_type = #{studyType}
and s.study_category_id in
#{item}
and s.study_startdate >= #{studyStartdate} and s.study_enddate <= #{studyEnddate}
and s.study_title like concat("%", #{studyTitle}, "%")
group by s.study_id
studyRegistdate desc
studyLike desc
studyView desc
limit #{startItem}, #{itemCnt}
