mapper.select:
select user_id, user_pw
from user
where user_id = #{user_id} and user_pw = #{user_pw}

select user_refresh_token
from user
where user_id = #{user_id}

mapper.update:
update user
set user_refresh_token = #{user_refresh_token}
where user_id = #{user_id}

update user
set user_refresh_token = #{user_refresh_token, jdbcType=VARCHAR}
where user_id = #{user_id}
